public class Clothes {
	private String name;
	private String id;
	private int price;

	public Clothes(String name, String id, int price) {
		// TODO Auto-generated constructor stub
		this.name = name;
		this.id = id;
		this.price = price;
	}

	public Object getName() {
		// TODO Auto-generated method stub
		return this.name;
	}

	public Object id() {
		// TODO Auto-generated method stub
		return id;
	}
	
	public int price() {
		// TODO Auto-generated method stub
		return this.price;
	}
}
